import React, { useState } from "react";
import axios from "axios";
import "../App.css";

// Placeholder API fonksiyonları
const fetchDealers = async () => [];
const fetchSubDealers = async () => [];
const fetchServices = async () => [];
const fetchAccounts = async () => [];
const fetchReports = async () => [];
const fetchUsers = async () => [];
const addUser = async (user) => {
  // Backend API'ye yeni kullanıcı ekle
  try {
    const res = await axios.post(
      "http://localhost:5000/api/auth/register",
      user
    );
    return res.data;
  } catch (err) {
    alert("Kullanıcı eklenemedi! " + (err.response?.data?.message || ""));
    return null;
  }
};

const menuItems = [
  { key: "dealers", label: "Ana Bayiler", icon: "🏢" },
  { key: "subdealers", label: "Alt Bayiler", icon: "🏬" },
  { key: "services", label: "Yetkili Servisler", icon: "🛠️" },
  { key: "accounts", label: "Cari Hesap Akışı", icon: "💳" },
  { key: "reports", label: "Raporlama", icon: "📊" },
  { key: "users", label: "Kullanıcı Yönetimi", icon: "👤" },
  { key: "logout", label: "Çıkış", icon: "🚪" },
];

export default function AdminPanelFull() {
  const [selected, setSelected] = useState("dealers");
  const [showAddUser, setShowAddUser] = useState(false);
  const [users, setUsers] = useState([
    { name: "superadmin", role: "Admin" },
    { name: "main_dealer", role: "Ana Bayi" },
    { name: "sub_dealer", role: "Alt Bayi" },
    { name: "admintest", role: "Admin" },
  ]);
  // Kullanıcı ekleme formu için state
  const [newUser, setNewUser] = useState({
    name: "",
    role: "Admin",
    email: "",
    password: "",
  });

  // Örnek veri
  const dealers = [
    { name: "main_dealer", region: "İstanbul" },
    { name: "main_dealer2", region: "Ankara" },
  ];
  const subdealers = [{ name: "sub_dealer", parent: "main_dealer" }];
  const services = [{ name: "servis1", parent: "main_dealer" }];
  const accounts = [{ gelir: 12000, gider: 8000, bakiye: 4000 }];
  const reports = [
    { type: "Satış", tutar: 5000, tarih: "16.09.2025" },
    { type: "Servis", tutar: 2000, tarih: "16.09.2025" },
  ];

  return (
    <div className="admin-layout">
      <aside className="sidebar">
        <h3>Admin Paneli</h3>
        <ul>
          {menuItems.map((item) => (
            <li
              key={item.key}
              className={selected === item.key ? "active" : ""}
              onClick={() => setSelected(item.key)}
              style={{ display: "flex", alignItems: "center", gap: "8px" }}
            >
              <span>{item.icon}</span> {item.label}
            </li>
          ))}
        </ul>
      </aside>
      <main className="main-content">
        <header
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "2rem",
            background: "#fff",
            padding: "1rem 2rem",
            borderRadius: "8px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
          }}
        >
          <span
            style={{ fontWeight: "bold", fontSize: "1.2rem", color: "#2a3a5e" }}
          >
            Hoşgeldiniz, Admin
          </span>
          <button className="btn btn-danger">Çıkış Yap</button>
        </header>
        <div
          style={{
            background: "#fff",
            borderRadius: "8px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
            padding: "2rem",
            minHeight: "400px",
          }}
        >
          {selected === "dealers" && (
            <>
              <h2 className="panel-title">Ana Bayiler</h2>
              <table className="table">
                <thead>
                  <tr>
                    <th>Ad</th>
                    <th>Bölge</th>
                  </tr>
                </thead>
                <tbody>
                  {dealers.map((d, i) => (
                    <tr key={i}>
                      <td>{d.name}</td>
                      <td>{d.region}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </>
          )}
          {selected === "subdealers" && (
            <>
              <h2 className="panel-title">Alt Bayiler</h2>
              <table className="table">
                <thead>
                  <tr>
                    <th>Ad</th>
                    <th>Üst Bayi</th>
                  </tr>
                </thead>
                <tbody>
                  {subdealers.map((d, i) => (
                    <tr key={i}>
                      <td>{d.name}</td>
                      <td>{d.parent}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </>
          )}
          {selected === "services" && (
            <>
              <h2 className="panel-title">Yetkili Servisler</h2>
              <table className="table">
                <thead>
                  <tr>
                    <th>Ad</th>
                    <th>Üst Bayi</th>
                  </tr>
                </thead>
                <tbody>
                  {services.map((s, i) => (
                    <tr key={i}>
                      <td>{s.name}</td>
                      <td>{s.parent}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </>
          )}
          {selected === "accounts" && (
            <>
              <h2 className="panel-title">Cari Hesap Akışı</h2>
              <table className="table">
                <thead>
                  <tr>
                    <th>Gelir</th>
                    <th>Gider</th>
                    <th>Bakiye</th>
                  </tr>
                </thead>
                <tbody>
                  {accounts.map((a, i) => (
                    <tr key={i}>
                      <td>{a.gelir}</td>
                      <td>{a.gider}</td>
                      <td>{a.bakiye}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div style={{ margin: "2rem 0" }}>
                <h4>Grafik (placeholder)</h4>
                <canvas
                  id="cariChart"
                  style={{
                    width: "100%",
                    height: "180px",
                    background: "#f8f9fa",
                    borderRadius: "8px",
                  }}
                ></canvas>
              </div>
            </>
          )}
          {selected === "reports" && (
            <>
              <h2 className="panel-title">Raporlama</h2>
              <table className="table">
                <thead>
                  <tr>
                    <th>Tür</th>
                    <th>Tutar</th>
                    <th>Tarih</th>
                  </tr>
                </thead>
                <tbody>
                  {reports.map((r, i) => (
                    <tr key={i}>
                      <td>{r.type}</td>
                      <td>{r.tutar}</td>
                      <td>{r.tarih}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div style={{ margin: "2rem 0" }}>
                <button className="btn btn-success">Excel İndir</button>
                <button
                  className="btn btn-primary"
                  style={{ marginLeft: "1rem" }}
                >
                  PDF İndir
                </button>
              </div>
            </>
          )}
          {selected === "users" && (
            <>
              <h2 className="panel-title">Kullanıcı Yönetimi</h2>
              <button
                className="btn btn-primary mb-2"
                onClick={() => setShowAddUser(true)}
              >
                Yeni Kullanıcı Ekle
              </button>
              <table className="table">
                <thead>
                  <tr>
                    <th>Ad</th>
                    <th>Rol</th>
                    <th>İşlem</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((u, i) => (
                    <tr key={i}>
                      <td>{u.name}</td>
                      <td>{u.role}</td>
                      <td>
                        <button className="btn btn-sm btn-warning">
                          Düzenle
                        </button>{" "}
                        <button className="btn btn-sm btn-danger">Sil</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {showAddUser && (
                <div
                  style={{
                    marginTop: "2rem",
                    background: "#f8f9fa",
                    padding: "1.5rem",
                    borderRadius: "8px",
                    boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                  }}
                >
                  <h4>Yeni Kullanıcı Ekle</h4>
                  <form
                    onSubmit={async (e) => {
                      e.preventDefault();
                      const userToAdd = { ...newUser };
                      const result = await addUser(userToAdd);
                      if (result) {
                        setUsers([...users, { name: result.name, role: result.role }]);
                        setShowAddUser(false);
                        setNewUser({ name: "", role: "Admin", email: "", password: "" });
                        alert("Kullanıcı başarıyla eklendi!");
                      }
                    }}
                  >
                    <div className="mb-3">
                      <label>Ad</label>
                      <input
                        type="text"
                        className="form-control"
                        required
                        value={newUser.name}
                        onChange={(e) =>
                          setNewUser({ ...newUser, name: e.target.value })
                        }
                      />
                    </div>
                    <div className="mb-3">
                      <label>Rol</label>
                      <select
                        className="form-control"
                        required
                        value={newUser.role}
                        onChange={(e) =>
                          setNewUser({ ...newUser, role: e.target.value })
                        }
                      >
                        <option>Admin</option>
                        <option>Ana Bayi</option>
                        <option>Alt Bayi</option>
                        <option>Servis</option>
                      </select>
                    </div>
                    <div className="mb-3">
                      <label>E-posta</label>
                      <input
                        type="email"
                        className="form-control"
                        required
                        value={newUser.email}
                        onChange={(e) =>
                          setNewUser({ ...newUser, email: e.target.value })
                        }
                      />
                    </div>
                    <div className="mb-3">
                      <label>Şifre</label>
                      <input
                        type="password"
                        className="form-control"
                        required
                        value={newUser.password}
                        onChange={(e) =>
                          setNewUser({ ...newUser, password: e.target.value })
                        }
                      />
                    </div>
                    <button className="btn btn-success" type="submit">
                      Kaydet
                    </button>
                    <button
                      className="btn btn-secondary"
                      type="button"
                      style={{ marginLeft: "1rem" }}
                      onClick={() => setShowAddUser(false)}
                    >
                      İptal
                    </button>
                  </form>
                </div>
              )}
            </>
          )}
          {selected === "logout" && (
            <div style={{ textAlign: "center", padding: "4rem" }}>
              <h2>Çıkış yapıldı.</h2>
              <p>Güvenli çıkış yaptınız.</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}
