import React, { useState } from "react";
import axios from "axios";
import "../App.css";

// Placeholder API fonksiyonlarÄ±
const fetchDealers = async () => [];
const fetchSubDealers = async () => [];
const fetchServices = async () => [];
const fetchAccounts = async () => [];
const fetchReports = async () => [];
const fetchUsers = async () => [];
const addUser = async (user) => {
  // Backend API'ye yeni kullanÄ±cÄ± ekle
  try {
    const res = await axios.post(
      "http://localhost:5000/api/auth/register",
      user
    );
    return res.data;
  } catch (err) {
    alert("KullanÄ±cÄ± eklenemedi! " + (err.response?.data?.message || ""));
    return null;
  }
};

const menuItems = [
  { key: "dealers", label: "Ana Bayiler", icon: "ğŸ¢" },
  { key: "subdealers", label: "Alt Bayiler", icon: "ğŸ¬" },
  { key: "services", label: "Yetkili Servisler", icon: "ğŸ› ï¸" },
  { key: "accounts", label: "Cari Hesap AkÄ±ÅŸÄ±", icon: "ğŸ’³" },
  { key: "reports", label: "Raporlama", icon: "ğŸ“Š" },
  { key: "users", label: "KullanÄ±cÄ± YÃ¶netimi", icon: "ğŸ‘¤" },
  { key: "logout", label: "Ã‡Ä±kÄ±ÅŸ", icon: "ğŸšª" },
];

export default function AdminPanelFull({ user, token }) {
  // Mobil sidebar aÃ§/kapa
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [selected, setSelected] = useState("dealers");
  const [showAddUser, setShowAddUser] = useState(false);
  const [users, setUsers] = useState([
    { name: "superadmin", role: "Admin" },
    { name: "main_dealer", role: "Ana Bayi" },
    { name: "sub_dealer", role: "Alt Bayi" },
    { name: "admintest", role: "Admin" },
  ]);
  const [newUser, setNewUser] = useState({
    name: "",
    role: "Admin",
    email: "",
    password: "",
  });
  const [loggedOut, setLoggedOut] = useState(false);

  // Panelde kullanÄ±lan veri tablolarÄ± iÃ§in state tanÄ±mlarÄ±
  const [dealers, setDealers] = useState([
    { name: "Bayi A", region: "Ä°stanbul" },
    { name: "Bayi B", region: "Ankara" },
  ]);
  const [subdealers, setSubDealers] = useState([
    { name: "Alt Bayi 1", parent: "Bayi A" },
    { name: "Alt Bayi 2", parent: "Bayi B" },
  ]);
  const [services, setServices] = useState([
    { name: "Servis 1", parent: "Bayi A" },
    { name: "Servis 2", parent: "Bayi B" },
  ]);
  const [accounts, setAccounts] = useState([
    { gelir: 10000, gider: 5000, bakiye: 5000 },
    { gelir: 8000, gider: 3000, bakiye: 5000 },
  ]);
  const [reports, setReports] = useState([
    { type: "SatÄ±ÅŸ", tutar: 12000, tarih: "2024-06-01" },
    { type: "Servis", tutar: 4000, tarih: "2024-06-02" },
  ]);

  if (loggedOut) {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh",
        }}
      >
        <h2>Ã‡Ä±kÄ±ÅŸ yapÄ±ldÄ±.</h2>
        <p>GÃ¼venli Ã§Ä±kÄ±ÅŸ yaptÄ±nÄ±z.</p>
        <a href="/" className="btn btn-primary">
          GiriÅŸe DÃ¶n
        </a>
      </div>
    );
  }

  return (
    <div className="admin-layout" style={{ position: "relative" }}>
      {/* Mobilde hamburger butonu */}
      <button
        className="sidebar-toggle"
        onClick={() => setSidebarOpen((v) => !v)}
      >
        â˜°
      </button>
      {/* SaÄŸ Ã¼stte HoÅŸgeldiniz ve Ã‡Ä±kÄ±ÅŸ */}
      <div
        style={{
          position: "absolute",
          top: 24,
          right: 32,
          display: "flex",
          alignItems: "center",
          gap: "1rem",
          background: "#fff",
          padding: "0.7rem 1.5rem",
          borderRadius: "8px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
          zIndex: 10,
        }}
      >
        <span
          style={{
            fontWeight: "bold",
            fontSize: "1.1rem",
            color: "#2a3a5e",
          }}
        >
          HoÅŸgeldiniz, {user?.name || "Admin"}
        </span>
        <button className="btn btn-danger" onClick={() => setLoggedOut(true)}>
          Ã‡Ä±kÄ±ÅŸ Yap
        </button>
      </div>
      <aside
        className={`sidebar${sidebarOpen ? " open" : ""}`}
      >
        <h3>Admin Paneli</h3>
        <ul>
          {menuItems.map((item) => (
            <li
              key={item.key}
              className={selected === item.key ? "active" : ""}
              onClick={() =>
                item.key === "logout"
                  ? setLoggedOut(true)
                  : setSelected(item.key)
              }
              style={{ display: "flex", alignItems: "center", gap: "8px" }}
            >
              <span>{item.icon}</span> {item.label}
            </li>
          ))}
        </ul>
      </aside>
      <main className="main-content">
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "2rem",
            marginTop: "3.5rem",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              flex: "1 1 700px",
              minWidth: "500px",
              maxWidth: "900px",
              background: "#fff",
              borderRadius: "20px",
              boxShadow: "0 6px 24px rgba(0,0,0,0.10)",
              padding: "3rem 2.5rem",
              marginBottom: "2rem",
              marginTop: "1rem",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {selected === "dealers" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "700",
                    fontSize: "2rem",
                    textAlign: "center",
                    marginBottom: "2rem",
                  }}
                >
                  Ana Bayiler
                </h2>
                <table
                  className="table"
                  style={{
                    width: "80%",
                    margin: "0 auto",
                    fontSize: "1.15rem",
                    background: "#f8f9fa",
                    borderRadius: "12px",
                    boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                  }}
                >
                  <thead>
                    <tr style={{ textAlign: "center" }}>
                      <th>Ad</th>
                      <th>BÃ¶lge</th>
                    </tr>
                  </thead>
                  <tbody>
                    {dealers.map((d, i) => (
                      <tr key={i} style={{ textAlign: "center" }}>
                        <td>{d.name}</td>
                        <td>{d.region}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "subdealers" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "700",
                    fontSize: "2rem",
                    textAlign: "center",
                    marginBottom: "2rem",
                  }}
                >
                  Alt Bayiler
                </h2>
                <table
                  className="table"
                  style={{
                    width: "80%",
                    margin: "0 auto",
                    fontSize: "1.15rem",
                    background: "#f8f9fa",
                    borderRadius: "12px",
                    boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                  }}
                >
                  <thead>
                    <tr style={{ textAlign: "center" }}>
                      <th>Ad</th>
                      <th>Ãœst Bayi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {subdealers.map((d, i) => (
                      <tr key={i} style={{ textAlign: "center" }}>
                        <td>{d.name}</td>
                        <td>{d.parent}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "services" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "700",
                    fontSize: "2rem",
                    textAlign: "center",
                    marginBottom: "2rem",
                  }}
                >
                  Yetkili Servisler
                </h2>
                <table
                  className="table"
                  style={{
                    width: "80%",
                    margin: "0 auto",
                    fontSize: "1.15rem",
                    background: "#f8f9fa",
                    borderRadius: "12px",
                    boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                  }}
                >
                  <thead>
                    <tr style={{ textAlign: "center" }}>
                      <th>Ad</th>
                      <th>Ãœst Bayi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {services.map((s, i) => (
                      <tr key={i} style={{ textAlign: "center" }}>
                        <td>{s.name}</td>
                        <td>{s.parent}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "accounts" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "700",
                    fontSize: "2rem",
                    textAlign: "center",
                    marginBottom: "2rem",
                  }}
                >
                  Cari Hesap AkÄ±ÅŸÄ±
                </h2>
                <table
                  className="table"
                  style={{
                    width: "80%",
                    margin: "0 auto",
                    fontSize: "1.15rem",
                    background: "#f8f9fa",
                    borderRadius: "12px",
                    boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                  }}
                >
                  <thead>
                    <tr style={{ textAlign: "center" }}>
                      <th>Gelir</th>
                      <th>Gider</th>
                      <th>Bakiye</th>
                    </tr>
                  </thead>
                  <tbody>
                    {accounts.map((a, i) => (
                      <tr key={i} style={{ textAlign: "center" }}>
                        <td>{a.gelir}</td>
                        <td>{a.gider}</td>
                        <td>{a.bakiye}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div
                  style={{
                    margin: "2rem 0",
                    width: "80%",
                    marginLeft: "auto",
                    marginRight: "auto",
                  }}
                >
                  <h4 style={{ textAlign: "center" }}>Grafik (placeholder)</h4>
                  <canvas
                    id="cariChart"
                    style={{
                      width: "100%",
                      height: "180px",
                      background: "#f8f9fa",
                      borderRadius: "8px",
                    }}
                  ></canvas>
                </div>
              </>
            )}
            {selected === "reports" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "700",
                    fontSize: "2rem",
                    textAlign: "center",
                    marginBottom: "2rem",
                  }}
                >
                  Raporlama
                </h2>
