import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AnaBayiPanel({ user, token }) {
  const [selected, setSelected] = useState("altbayiler");
  const [altBayiler, setAltBayiler] = useState([]);
  const [servisler, setServisler] = useState([]);
  const [satislar, setSatislar] = useState([]);
  const [cariHesaplar, setCariHesaplar] = useState([]);
  const [raporlar, setRaporlar] = useState([]);

  // Sadece kendi bölgesine bağlı alt bayiler ve servisler
  useEffect(() => {
    async function fetchData() {
      const region = user?.region;
      const altBayilerRes = await axios.get(`/api/altbayi?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAltBayiler(altBayilerRes.data);
      const servislerRes = await axios.get(`/api/servis?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServisler(servislerRes.data);
      const satislarRes = await axios.get(`/api/satis?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSatislar(satislarRes.data);
      const cariRes = await axios.get(`/api/cari?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCariHesaplar(cariRes.data);
      const raporRes = await axios.get(`/api/rapor?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRaporlar(raporRes.data);
    }
    if (user?.region) fetchData();
  }, [user, token]);

  return (
    <div
      className="anabayi-layout"
      style={{ display: "flex", minHeight: "100vh" }}
    >
      <aside
        className="sidebar"
        style={{
          width: 220,
          background: "#f8f9fa",
          padding: "2rem 1rem",
          boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
        }}
      >
        <h3 style={{ textAlign: "center", marginBottom: "2rem" }}>
          Ana Bayi Paneli
        </h3>
        <ul style={{ listStyle: "none", padding: 0 }}>
          <li
            className={selected === "altbayiler" ? "active" : ""}
            onClick={() => setSelected("altbayiler")}
          >
            Alt Bayiler
          </li>
          <li
            className={selected === "servisler" ? "active" : ""}
            onClick={() => setSelected("servisler")}
          >
            Yetkili Servisler
          </li>
          <li
            className={selected === "satis" ? "active" : ""}
            onClick={() => setSelected("satis")}
          >
            Satış İşlemleri
          </li>
          <li
            className={selected === "cari" ? "active" : ""}
            onClick={() => setSelected("cari")}
          >
            Cari Hesaplar
          </li>
          <li
            className={selected === "rapor" ? "active" : ""}
            onClick={() => setSelected("rapor")}
          >
            Raporlama
          </li>
        </ul>
      </aside>
      <main style={{ flex: 1, background: "#fff", padding: "2rem 3rem" }}>
        {selected === "altbayiler" && (
          <>
            <h2>Alt Bayiler</h2>
            <table
              className="table"
              style={{ width: "100%", marginBottom: "2rem" }}
            >
              <thead>
                <tr>
                  <th>Ad</th>
                  <th>Bölge</th>
                  <th>İşlem</th>
                </tr>
              </thead>
              <tbody>
                {altBayiler.map((b, i) => (
                  <tr key={i}>
                    <td>{b.name}</td>
                    <td>{b.region}</td>
                    <td>
                      {/* Sadece kendi eklediği alt bayilerde düzenleme/silme */}
                      {b.createdBy === user._id ? (
                        <>
                          <button
                            className="btn btn-sm btn-warning"
                            onClick={() => setEditingAltBayi(b)}
                          >
                            Düzenle
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            style={{ marginLeft: 8 }}
                            onClick={() => handleDeleteAltBayi(b._id)}
                          >
                            Sil
                          </button>
                        </>
                      ) : (
                        <span style={{ color: "#888" }}>
                          Sadece izlenebilir
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Ekleme formu */}
            <form onSubmit={handleAddAltBayi} style={{ marginBottom: "2rem" }}>
              <div style={{ display: "flex", gap: "1rem" }}>
                <input
                  type="text"
                  placeholder="Ad"
                  value={newAltBayi.name}
                  onChange={(e) =>
                    setNewAltBayi({ ...newAltBayi, name: e.target.value })
                  }
                  required
                />
                <input
                  type="text"
                  placeholder="Bölge"
                  value={newAltBayi.region}
                  onChange={(e) =>
                    setNewAltBayi({ ...newAltBayi, region: e.target.value })
                  }
                  required
                />
                <button className="btn btn-success" type="submit">
                  Ekle
                </button>
              </div>
            </form>
            {/* Düzenleme formu */}
            {editingAltBayi && (
              <form
                onSubmit={handleEditAltBayi}
                style={{ marginBottom: "2rem" }}
              >
                <div style={{ display: "flex", gap: "1rem" }}>
                  <input
                    type="text"
                    value={editingAltBayi.name}
                    onChange={(e) =>
                      setEditingAltBayi({
                        ...editingAltBayi,
                        name: e.target.value,
                      })
                    }
                    required
                  />
                  <input
                    type="text"
                    value={editingAltBayi.region}
                    onChange={(e) =>
                      setEditingAltBayi({
                        ...editingAltBayi,
                        region: e.target.value,
                      })
                    }
                    required
                  />
                  <button className="btn btn-primary" type="submit">
                    Kaydet
                  </button>
                  <button
                    className="btn btn-secondary"
                    type="button"
                    onClick={() => setEditingAltBayi(null)}
                  >
                    İptal
                  </button>
                </div>
              </form>
            )}
          </>
        )}
        {selected === "servisler" && (
          <>
            <h2>Yetkili Servisler</h2>
            <table
              className="table"
              style={{ width: "100%", marginBottom: "2rem" }}
            >
              <thead>
                <tr>
                  <th>Ad</th>
                  <th>Bölge</th>
                  <th>İşlem</th>
                </tr>
              </thead>
              <tbody>
                {servisler.map((s, i) => (
                  <tr key={i}>
                    <td>{s.name}</td>
                    <td>{s.region}</td>
                    <td>
                      {/* Sadece kendi eklediği servislerde düzenleme/silme */}
                      {s.createdBy === user._id ? (
                        <>
                          <button
                            className="btn btn-sm btn-warning"
                            onClick={() => setEditingServis(s)}
                          >
                            Düzenle
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            style={{ marginLeft: 8 }}
                            onClick={() => handleDeleteServis(s._id)}
                          >
                            Sil
                          </button>
                        </>
                      ) : (
                        <span style={{ color: "#888" }}>
                          Sadece izlenebilir
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Ekleme formu */}
            <form onSubmit={handleAddServis} style={{ marginBottom: "2rem" }}>
              <div style={{ display: "flex", gap: "1rem" }}>
                <input
                  type="text"
                  placeholder="Ad"
                  value={newServis.name}
                  onChange={(e) =>
                    setNewServis({ ...newServis, name: e.target.value })
                  }
                  required
                />
                <input
                  type="text"
                  placeholder="Bölge"
                  value={newServis.region}
                  onChange={(e) =>
                    setNewServis({ ...newServis, region: e.target.value })
                  }
                  required
                />
                <button className="btn btn-success" type="submit">
                  Ekle
                </button>
              </div>
            </form>
            {/* Düzenleme formu */}
            {editingServis && (
              <form
                onSubmit={handleEditServis}
                style={{ marginBottom: "2rem" }}
              >
                <div style={{ display: "flex", gap: "1rem" }}>
                  <input
                    type="text"
                    value={editingServis.name}
                    onChange={(e) =>
                      setEditingServis({
                        ...editingServis,
                        name: e.target.value,
                      })
                    }
                    required
                  />
                  <input
                    type="text"
                    value={editingServis.region}
                    onChange={(e) =>
                      setEditingServis({
                        ...editingServis,
                        region: e.target.value,
                      })
                    }
                    required
                  />
                  <button className="btn btn-primary" type="submit">
                    Kaydet
                  </button>
                  <button
                    className="btn btn-secondary"
                    type="button"
                    onClick={() => setEditingServis(null)}
                  >
                    İptal
                  </button>
                </div>
              </form>
            )}
          </>
        )}
        {selected === "satis" && (
          <>
            <h2>Satış İşlemleri</h2>
            <table
              className="table"
              style={{ width: "100%", marginBottom: "2rem" }}
            >
              <thead>
                <tr>
                  <th>Ürün</th>
                  <th>Miktar</th>
                  <th>Tutar</th>
                  <th>Tarih</th>
                </tr>
              </thead>
              <tbody>
                {satislar.map((s, i) => (
                  <tr key={i}>
                    <td>{s.urun}</td>
                    <td>{s.miktar}</td>
                    <td>{s.tutar}</td>
                    <td>{s.tarih}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Sipariş yönetimi ve ürün satışı formları buraya eklenebilir */}
          </>
        )}
        {selected === "cari" && (
          <>
            <h2>Cari Hesaplar</h2>
            <table
              className="table"
              style={{ width: "100%", marginBottom: "2rem" }}
            >
              <thead>
                <tr>
                  <th>Gelir</th>
                  <th>Gider</th>
                  <th>Bakiye</th>
                </tr>
              </thead>
              <tbody>
                {cariHesaplar.map((c, i) => (
                  <tr key={i}>
                    <td>{c.gelir}</td>
                    <td>{c.gider}</td>
                    <td>{c.bakiye}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Grafik ve ek hareketler buraya eklenebilir */}
          </>
        )}
        {selected === "rapor" && (
          <>
            <h2>Raporlama</h2>
            <table
              className="table"
              style={{ width: "100%", marginBottom: "2rem" }}
            >
              <thead>
                <tr>
                  <th>Tür</th>
                  <th>Tutar</th>
                  <th>Tarih</th>
                </tr>
              </thead>
              <tbody>
                {raporlar.map((r, i) => (
                  <tr key={i}>
                    <td>{r.type}</td>
                    <td>{r.tutar}</td>
                    <td>{r.tarih}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Satış ve servis performans raporu grafik/özet buraya eklenebilir */}
          </>
        )}
      </main>
    </div>
  );
}
