                  </tr>
                </thead>
                <tbody>
                  <tr style={{ textAlign: "center" }}>
                    <td style={{ padding: "10px" }}>Buzdolabı</td>
                    <td style={{ padding: "10px" }}>10</td>
                    <td style={{ padding: "10px" }}>₺90.000</td>
                  </tr>
                  <tr style={{ textAlign: "center", background: "#e3f2fd" }}>
                    <td style={{ padding: "10px" }}>Çamaşır</td>
                    <td style={{ padding: "10px" }}>6</td>
                    <td style={{ padding: "10px" }}>₺42.000</td>
                  </tr>
                  <tr style={{ textAlign: "center" }}>
                    <td style={{ padding: "10px" }}>TV</td>
                    <td style={{ padding: "10px" }}>4</td>
                    <td style={{ padding: "10px" }}>₺48.000</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div className="table-responsive mb-4">
              <table
                className="table table-bordered table-hover satis-table"
                style={{
                  background: "#fff",
                  borderRadius: 12,
                  boxShadow: "0 2px 8px rgba(0,123,255,0.10)",
                  overflow: "hidden",
                  width: "80%",
                  minWidth: 300,
                  maxWidth: 700,
                  margin: "0 auto",
                  marginBottom: "2rem",
                }}
              >
                <thead className="table-primary">
                  <tr style={{ textAlign: "center" }}>
                    <th style={{ padding: "12px" }}>Tür</th>
                    <th style={{ padding: "12px" }}>Toplam</th>
                  </tr>
                </thead>
                <tbody>
                  <tr style={{ textAlign: "center" }}>
                    <td style={{ padding: "10px" }}>Gelir</td>
                    <td style={{ padding: "10px" }}>₺210.000</td>
                  </tr>
                  <tr style={{ textAlign: "center", background: "#e3f2fd" }}>
                    <td style={{ padding: "10px" }}>Gider</td>
                    <td style={{ padding: "10px" }}>₺50.000</td>
                  </tr>
                  <tr style={{ textAlign: "center" }}>
                    <td style={{ padding: "10px" }}>Bakiye</td>
                    <td style={{ padding: "10px" }}>₺160.000</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div
              style={{
                height: 180,
                background: "#e9ecef",
                borderRadius: 8,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                color: "#888",
                fontSize: 20,
                width: "80%",
                minWidth: 300,
                maxWidth: 700,
                margin: "0 auto",
              }}
            >
              Grafik Alanı (placeholder)
            </div>
            <div
              className="mt-4 d-flex gap-2 justify-content-center"
              style={{
                width: "80%",
                minWidth: 300,
                maxWidth: 700,
                margin: "0 auto",
              }}
            >
              <button className="btn btn-danger">PDF İndir</button>
              <button
                className="btn btn-success"
                style={{ marginLeft: "1rem" }}
              >
                Excel İndir
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
