import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import AdminPanel from "./pages/AdminPanel";
import AnaBayiPanel from "./pages/AnaBayiPanel";
import AltBayiPanel from "./pages/AltBayiPanel";
import ServisPanel from "./pages/ServisPanel";
import Login from "./pages/Login";

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);

  const handleLogin = (user, token) => {
    setUser(user);
    setToken(token);
  };

  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  let panel;
  if (user.role === "admin") panel = <AdminPanel user={user} token={token} />;
  else if (user.role === "anabayi")
    panel = <AnaBayiPanel user={user} token={token} />;
  else if (user.role === "altbayi")
    panel = <AltBayiPanel user={user} token={token} />;
  else if (user.role === "servis")
    panel = <ServisPanel user={user} token={token} />;

  return (
    <Router>
      <Routes>
        <Route path="/" element={panel} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
