import React, { useState } from "react";
import axios from "axios";
import "../App.css";

// Placeholder API fonksiyonlarÄ±
const fetchDealers = async () => [];
const fetchSubDealers = async () => [];
const fetchServices = async () => [];
const fetchAccounts = async () => [];
const fetchReports = async () => [];
const fetchUsers = async () => [];
const addUser = async (user) => {
  // Backend API'ye yeni kullanÄ±cÄ± ekle
  try {
    const res = await axios.post(
      "http://localhost:5000/api/auth/register",
      user
    );
    return res.data;
  } catch (err) {
    alert("KullanÄ±cÄ± eklenemedi! " + (err.response?.data?.message || ""));
    return null;
  }
};

const menuItems = [
  { key: "dealers", label: "Ana Bayiler", icon: "ğŸ¢" },
  { key: "subdealers", label: "Alt Bayiler", icon: "ğŸ¬" },
  { key: "services", label: "Yetkili Servisler", icon: "ğŸ› ï¸" },
  { key: "accounts", label: "Cari Hesap AkÄ±ÅŸÄ±", icon: "ğŸ’³" },
  { key: "reports", label: "Raporlama", icon: "ğŸ“Š" },
  { key: "users", label: "KullanÄ±cÄ± YÃ¶netimi", icon: "ğŸ‘¤" },
  { key: "logout", label: "Ã‡Ä±kÄ±ÅŸ", icon: "ğŸšª" },
];

export default function AdminPanelFull({ user, token }) {
  const [selected, setSelected] = useState("dealers");
  const [showAddUser, setShowAddUser] = useState(false);
  const [users, setUsers] = useState([
    { name: "superadmin", role: "Admin" },
    { name: "main_dealer", role: "Ana Bayi" },
    { name: "sub_dealer", role: "Alt Bayi" },
    { name: "admintest", role: "Admin" },
  ]);
  const [newUser, setNewUser] = useState({
    name: "",
    role: "Admin",
    email: "",
    password: "",
  });
  const [loggedOut, setLoggedOut] = useState(false);

  // Panelde kullanÄ±lan veri tablolarÄ± iÃ§in state tanÄ±mlarÄ±
  const [dealers, setDealers] = useState([
    { name: "Bayi A", region: "Ä°stanbul" },
    { name: "Bayi B", region: "Ankara" },
  ]);
  const [subdealers, setSubDealers] = useState([
    { name: "Alt Bayi 1", parent: "Bayi A" },
    { name: "Alt Bayi 2", parent: "Bayi B" },
  ]);
  const [services, setServices] = useState([
    { name: "Servis 1", parent: "Bayi A" },
    { name: "Servis 2", parent: "Bayi B" },
  ]);
  const [accounts, setAccounts] = useState([
    { gelir: 10000, gider: 5000, bakiye: 5000 },
    { gelir: 8000, gider: 3000, bakiye: 5000 },
  ]);
  const [reports, setReports] = useState([
    { type: "SatÄ±ÅŸ", tutar: 12000, tarih: "2024-06-01" },
    { type: "Servis", tutar: 4000, tarih: "2024-06-02" },
  ]);

  if (loggedOut) {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh",
        }}
      >
        <h2>Ã‡Ä±kÄ±ÅŸ yapÄ±ldÄ±.</h2>
        <p>GÃ¼venli Ã§Ä±kÄ±ÅŸ yaptÄ±nÄ±z.</p>
        <a href="/" className="btn btn-primary">
          GiriÅŸe DÃ¶n
        </a>
      </div>
    );
  }

  return (
    <div className="admin-layout">
      <aside className="sidebar">
        <h3>Admin Paneli</h3>
        <ul>
          {menuItems.map((item) => (
            <li
              key={item.key}
              className={selected === item.key ? "active" : ""}
              onClick={() =>
                item.key === "logout"
                  ? setLoggedOut(true)
                  : setSelected(item.key)
              }
              style={{ display: "flex", alignItems: "center", gap: "8px" }}
            >
              <span>{item.icon}</span> {item.label}
            </li>
          ))}
        </ul>
      </aside>
      <main className="main-content">
        <div style={{ position: "relative", marginBottom: "2rem" }}>
          <div
            style={{
              position: "absolute",
              top: 0,
              right: 0,
              display: "flex",
              alignItems: "center",
              gap: "1rem",
              background: "#fff",
              padding: "0.7rem 1.5rem",
              borderRadius: "8px",
              boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
            }}
          >
            <span
              style={{
                fontWeight: "bold",
                fontSize: "1.1rem",
                color: "#2a3a5e",
              }}
            >
              HoÅŸgeldiniz, {user?.name || "Admin"}
            </span>
            <button
              className="btn btn-danger"
              onClick={() => setLoggedOut(true)}
            >
              Ã‡Ä±kÄ±ÅŸ Yap
            </button>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "2rem",
            marginTop: "3.5rem",
          }}
        >
          <div
            style={{
              flex: "1",
              minWidth: "340px",
              background: "#fff",
              borderRadius: "16px",
              boxShadow: "0 4px 16px rgba(0,0,0,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            {selected === "dealers" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Ana Bayiler
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>BÃ¶lge</th>
                    </tr>
                  </thead>
                  <tbody>
                    {dealers.map((d, i) => (
                      <tr key={i}>
                        <td>{d.name}</td>
                        <td>{d.region}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "subdealers" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Alt Bayiler
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Ãœst Bayi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {subdealers.map((d, i) => (
                      <tr key={i}>
                        <td>{d.name}</td>
                        <td>{d.parent}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "services" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Yetkili Servisler
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Ãœst Bayi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {services.map((s, i) => (
                      <tr key={i}>
                        <td>{s.name}</td>
                        <td>{s.parent}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "accounts" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Cari Hesap AkÄ±ÅŸÄ±
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Gelir</th>
                      <th>Gider</th>
                      <th>Bakiye</th>
                    </tr>
                  </thead>
                  <tbody>
                    {accounts.map((a, i) => (
                      <tr key={i}>
                        <td>{a.gelir}</td>
                        <td>{a.gider}</td>
                        <td>{a.bakiye}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div style={{ margin: "2rem 0" }}>
                  <h4>Grafik (placeholder)</h4>
                  <canvas
                    id="cariChart"
                    style={{
                      width: "100%",
                      height: "180px",
                      background: "#f8f9fa",
                      borderRadius: "8px",
                    }}
                  ></canvas>
                </div>
              </>
            )}
            {selected === "reports" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Raporlama
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>TÃ¼r</th>
                      <th>Tutar</th>
                      <th>Tarih</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map((r, i) => (
                      <tr key={i}>
                        <td>{r.type}</td>
                        <td>{r.tutar}</td>
                        <td>{r.tarih}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div style={{ margin: "2rem 0" }}>
                  <button className="btn btn-success">Excel Ä°ndir</button>
                  <button
                    className="btn btn-primary"
                    style={{ marginLeft: "1rem" }}
                  >
                    PDF Ä°ndir
                  </button>
                </div>
              </>
            )}
            {selected === "users" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  KullanÄ±cÄ± YÃ¶netimi
                </h2>
                <button
                  className="btn btn-primary mb-2"
                  onClick={() => setShowAddUser(true)}
                >
                  Yeni KullanÄ±cÄ± Ekle
                </button>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Rol</th>
                      <th>Ä°ÅŸlem</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((u, i) => (
                      <tr key={i}>
                        <td>{u.name}</td>
                        <td>{u.role}</td>
                        <td>
                          <button className="btn btn-sm btn-warning">
                            DÃ¼zenle
                          </button>{" "}
                          <button className="btn btn-sm btn-danger">Sil</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                {showAddUser && (
                  <div
                    style={{
                      marginTop: "2rem",
                      background: "#f8f9fa",
                      padding: "1.5rem",
                      borderRadius: "8px",
                      boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                    }}
                  >
                    <h4>Yeni KullanÄ±cÄ± Ekle</h4>
                    <form
                      onSubmit={async (e) => {
                        e.preventDefault();
                        const userToAdd = { ...newUser };
                        const result = await addUser(userToAdd);
                        if (result) {
                          setUsers([
                            ...users,
                            { name: result.name, role: result.role },
                          ]);
                          setShowAddUser(false);
                          setNewUser({
                            name: "",
                            role: "Admin",
                            email: "",
                            password: "",
                          });
                          alert("KullanÄ±cÄ± baÅŸarÄ±yla eklendi!");
                        }
                      }}
                    >
                      <div className="mb-3">
                        <label>Ad</label>
                        <input
                          type="text"
                          className="form-control"
                          required
                          value={newUser.name}
                          onChange={(e) =>
                            setNewUser({ ...newUser, name: e.target.value })
                          }
                        />
                      </div>
                      <div className="mb-3">
                        <label>Rol</label>
                        <select
                          className="form-control"
                          required
                          value={newUser.role}
                          onChange={(e) =>
                            setNewUser({ ...newUser, role: e.target.value })
                          }
                        >
                          <option>Admin</option>
                          <option>Ana Bayi</option>
                          <option>Alt Bayi</option>
                          <option>Servis</option>
                        </select>
                      </div>
                      <div className="mb-3">
                        <label>E-posta</label>
                        <input
                          type="email"
                          className="form-control"
                          required
                          value={newUser.email}
                          onChange={(e) =>
                            setNewUser({ ...newUser, email: e.target.value })
                          }
                        />
                      </div>
                      <div className="mb-3">
                        <label>Åifre</label>
                        <input
                          type="password"
                          className="form-control"
                          required
                          value={newUser.password}
                          onChange={(e) =>
                            setNewUser({ ...newUser, password: e.target.value })
                          }
                        />
                      </div>
                      <button className="btn btn-success" type="submit">
                        Kaydet
                      </button>
                      <button
                        className="btn btn-secondary"
                        type="button"
                        style={{ marginLeft: "1rem" }}
                        onClick={() => setShowAddUser(false)}
                      >
                        Ä°ptal
                      </button>
                    </form>
                  </div>
                )}
              </>
            )}
            {selected === "logout" && (
              <div style={{ textAlign: "center", padding: "4rem" }}>
                <h2>Ã‡Ä±kÄ±ÅŸ yapÄ±ldÄ±.</h2>
                <p>GÃ¼venli Ã§Ä±kÄ±ÅŸ yaptÄ±nÄ±z.</p>
                <a href="/" className="btn btn-primary">
                  GiriÅŸe DÃ¶n
                </a>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}
