import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AnaBayiPanel({ user, token }) {
  const [selected, setSelected] = useState("altbayiler");
  const [altBayiler, setAltBayiler] = useState([]);
  const [servisler, setServisler] = useState([]);
  const [satislar, setSatislar] = useState([]);
  const [cariHesaplar, setCariHesaplar] = useState([]);
  const [raporlar, setRaporlar] = useState([]);
  const [newAltBayi, setNewAltBayi] = useState({ name: "", region: "" });
  const [editingAltBayi, setEditingAltBayi] = useState(null);
  const [newServis, setNewServis] = useState({ name: "", region: "" });
  const [editingServis, setEditingServis] = useState(null);

  // Sadece kendi bölgesine bağlı alt bayiler ve servisler
  useEffect(() => {
    async function fetchData() {
      const region = user?.region;
      const altBayilerRes = await axios.get(`/api/altbayi?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAltBayiler(altBayilerRes.data);
      const servislerRes = await axios.get(`/api/servis?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServisler(servislerRes.data);
      const satislarRes = await axios.get(`/api/satis?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSatislar(satislarRes.data);
      const cariRes = await axios.get(`/api/cari?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCariHesaplar(cariRes.data);
      const raporRes = await axios.get(`/api/rapor?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRaporlar(raporRes.data);
    }
    if (user?.region) fetchData();
  }, [user, token]);

  const handleAddAltBayi = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "/api/altbayi",
        { ...newAltBayi, createdBy: user._id },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAltBayiler([...altBayiler, res.data]);
      setNewAltBayi({ name: "", region: "" });
    } catch (err) {
      alert("Alt bayi eklenemedi!");
    }
  };

  const handleEditAltBayi = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(
        `/api/altbayi/${editingAltBayi._id}`,
        editingAltBayi,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAltBayiler(
        altBayiler.map((b) => (b._id === editingAltBayi._id ? res.data : b))
      );
      setEditingAltBayi(null);
    } catch (err) {
      alert("Alt bayi güncellenemedi!");
    }
  };

  const handleDeleteAltBayi = async (id) => {
    try {
      await axios.delete(`/api/altbayi/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAltBayiler(altBayiler.filter((b) => b._id !== id));
    } catch (err) {
      alert("Alt bayi silinemedi!");
    }
  };

  const handleAddServis = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "/api/servis",
        { ...newServis, createdBy: user._id },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setServisler([...servisler, res.data]);
      setNewServis({ name: "", region: "" });
    } catch (err) {
      alert("Servis eklenemedi!");
    }
  };

  const handleEditServis = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(
        `/api/servis/${editingServis._id}`,
        editingServis,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setServisler(
        servisler.map((s) => (s._id === editingServis._id ? res.data : s))
      );
      setEditingServis(null);
    } catch (err) {
      alert("Servis güncellenemedi!");
    }
  };

  const handleDeleteServis = async (id) => {
    try {
      await axios.delete(`/api/servis/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServisler(servisler.filter((s) => s._id !== id));
    } catch (err) {
      alert("Servis silinemedi!");
    }
  };

  return (
    <div
      className="anabayi-layout"
      style={{ display: "flex", minHeight: "100vh" }}
    >
      <aside
        className="sidebar"
        style={{
          width: 220,
          background: "#f8f9fa",
          padding: "2rem 1rem",
          boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
        }}
      >
        <h3
          style={{
            textAlign: "center",
            marginBottom: "2rem",
            fontWeight: 700,
            letterSpacing: 1,
            color: "#007bff",
          }}
        >
          Ana Bayi Paneli
        </h3>
        <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
          <li
            style={{
              padding: "0.75rem 1rem",
              cursor: "pointer",
              borderBottom: "1px solid #ddd",
              background: selected === "altbayiler" ? "#007bff" : "transparent",
              color: selected === "altbayiler" ? "#fff" : "#000",
            }}
            onClick={() => setSelected("altbayiler")}
          >
            Alt Bayiler
            <div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>
              Bölgenize bağlı alt bayileri görüntüleyin ve ekleyin.
            </div>
          </li>
          <li
            style={{
              padding: "0.75rem 1rem",
              cursor: "pointer",
              borderBottom: "1px solid #ddd",
              background: selected === "servisler" ? "#007bff" : "transparent",
              color: selected === "servisler" ? "#fff" : "#000",
            }}
            onClick={() => setSelected("servisler")}
          >
            Yetkili Servisler
            <div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>
              Bölgenizdeki yetkili servisleri yönetin.
            </div>
          </li>
          <li
            style={{
              padding: "0.75rem 1rem",
              cursor: "pointer",
              borderBottom: "1px solid #ddd",
              background: selected === "satis" ? "#007bff" : "transparent",
              color: selected === "satis" ? "#fff" : "#000",
            }}
            onClick={() => setSelected("satis")}
          >
            Satış İşlemleri
            <div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>
              Bölgenize ait satış hareketlerini inceleyin.
            </div>
          </li>
          <li
            style={{
              padding: "0.75rem 1rem",
              cursor: "pointer",
              borderBottom: "1px solid #ddd",
              background: selected === "cari" ? "#007bff" : "transparent",
              color: selected === "cari" ? "#fff" : "#000",
            }}
            onClick={() => setSelected("cari")}
          >
            Cari Hesaplar
            <div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>
              Gelir, gider ve bakiye durumunu takip edin.
            </div>
          </li>
          <li
            style={{
              padding: "0.75rem 1rem",
              cursor: "pointer",
              borderBottom: "1px solid #ddd",
              background: selected === "rapor" ? "#007bff" : "transparent",
              color: selected === "rapor" ? "#fff" : "#000",
            }}
            onClick={() => setSelected("rapor")}
          >
            Raporlama
            <div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>
              Performans ve finansal raporları görüntüleyin.
            </div>
          </li>
          <li
            style={{
              padding: "0.75rem 1rem",
              cursor: "pointer",
              borderBottom: "1px solid #ddd",
              background: "transparent",
              color: "#000",
            }}
            onClick={() => (window.location.href = "/")}
          >
            Çıkış Yap
          </li>
        </ul>
      </aside>
      <main style={{ flex: 1, background: "#fff", padding: "2rem 3rem" }}>
        {selected === "altbayiler" && (
          <div
            style={{
              background: "#f4f8ff",
              borderRadius: 12,
              boxShadow: "0 2px 12px rgba(0,123,255,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>
              Alt Bayiler
            </h2>
            <table
              className="table"
              style={{
                width: "100%",
                marginBottom: "2rem",
                background: "#fff",
                borderRadius: 8,
                boxShadow: "0 1px 6px rgba(0,0,0,0.04)",
              }}
            >
              <thead>
                <tr>
                  <th>Ad</th>
                  <th>Bölge</th>
                  <th>İşlem</th>
                </tr>
              </thead>
              <tbody>
                {altBayiler.map((b, i) => (
                  <tr key={i}>
                    <td>{b.name}</td>
                    <td>{b.region}</td>
                    <td>
                      {/* Sadece kendi eklediği alt bayilerde düzenleme/silme */}
                      {b.createdBy === user._id ? (
                        <>
                          <button
                            className="btn btn-sm btn-warning"
                            onClick={() => setEditingAltBayi(b)}
                          >
                            Düzenle
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            style={{ marginLeft: 8 }}
                            onClick={() => handleDeleteAltBayi(b._id)}
                          >
                            Sil
                          </button>
                        </>
                      ) : (
                        <span style={{ color: "#888" }}>
                          Sadece izlenebilir
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Ekleme formu */}
            <form onSubmit={handleAddAltBayi} style={{ marginBottom: "2rem" }}>
              <div
                style={{ display: "flex", gap: "1rem", alignItems: "center" }}
              >
                <span role="img" aria-label="ad" style={{ fontSize: 20 }}>
                  👤
                </span>
                <input
                  type="text"
                  placeholder="Alt Bayi Adı"
                  value={newAltBayi.name}
                  onChange={(e) =>
                    setNewAltBayi({ ...newAltBayi, name: e.target.value })
                  }
                  required
                  style={{
                    borderRadius: 6,
                    border: "1px solid #007bff",
                    padding: "0.5rem 1rem",
                  }}
                />
                <span role="img" aria-label="bölge" style={{ fontSize: 20 }}>
                  🌍
                </span>
                <input
                  type="text"
                  placeholder="Bölge"
                  value={newAltBayi.region}
                  onChange={(e) =>
                    setNewAltBayi({ ...newAltBayi, region: e.target.value })
                  }
                  required
                  style={{
                    borderRadius: 6,
                    border: "1px solid #007bff",
                    padding: "0.5rem 1rem",
                  }}
                />
                <button
                  className="btn btn-success"
                  type="submit"
                  style={{ borderRadius: 6, fontWeight: 600 }}
                >
                  + Alt Bayi Ekle
                </button>
              </div>
            </form>
            {/* Düzenleme formu */}
            {editingAltBayi && (
              <form
                onSubmit={handleEditAltBayi}
                style={{ marginBottom: "2rem" }}
              >
                <div style={{ display: "flex", gap: "1rem" }}>
                  <input
                    type="text"
                    value={editingAltBayi.name}
                    onChange={(e) =>
                      setEditingAltBayi({
                        ...editingAltBayi,
                        name: e.target.value,
                      })
                    }
                    required
                  />
                  <input
                    type="text"
                    value={editingAltBayi.region}
                    onChange={(e) =>
                      setEditingAltBayi({
                        ...editingAltBayi,
                        region: e.target.value,
                      })
                    }
                    required
                  />
                  <button className="btn btn-primary" type="submit">
                    Kaydet
                  </button>
                  <button
                    className="btn btn-secondary"
                    type="button"
                    onClick={() => setEditingAltBayi(null)}
                  >
                    İptal
                  </button>
                </div>
              </form>
            )}
          </div>
        )}
        {selected === "servisler" && (
          <div
            style={{
              background: "#f4f8ff",
              borderRadius: 12,
              boxShadow: "0 2px 12px rgba(0,123,255,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>
              Yetkili Servisler
            </h2>
            <table
              className="table"
              style={{
                width: "100%",
                marginBottom: "2rem",
                background: "#fff",
                borderRadius: 8,
                boxShadow: "0 1px 6px rgba(0,0,0,0.04)",
              }}
            >
              <thead>
                <tr>
                  <th>Ad</th>
                  <th>Bölge</th>
                  <th>İşlem</th>
                </tr>
              </thead>
              <tbody>
                {servisler.map((s, i) => (
                  <tr key={i}>
                    <td>{s.name}</td>
                    <td>{s.region}</td>
                    <td>
                      {/* Sadece kendi eklediği servislerde düzenleme/silme */}
                      {s.createdBy === user._id ? (
                        <>
                          <button
                            className="btn btn-sm btn-warning"
                            onClick={() => setEditingServis(s)}
                          >
                            Düzenle
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            style={{ marginLeft: 8 }}
                            onClick={() => handleDeleteServis(s._id)}
                          >
                            Sil
                          </button>
                        </>
                      ) : (
                        <span style={{ color: "#888" }}>
                          Sadece izlenebilir
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {/* Ekleme formu */}
            <form onSubmit={handleAddServis} style={{ marginBottom: "2rem" }}>
              <div
                style={{ display: "flex", gap: "1rem", alignItems: "center" }}
              >
                <span role="img" aria-label="ad" style={{ fontSize: 20 }}>
                  🔧
                </span>
                <input
                  type="text"
                  placeholder="Servis Adı"
                  value={newServis.name}
                  onChange={(e) =>
                    setNewServis({ ...newServis, name: e.target.value })
                  }
                  required
                  style={{
                    borderRadius: 6,
                    border: "1px solid #007bff",
                    padding: "0.5rem 1rem",
                  }}
                />
                <span role="img" aria-label="bölge" style={{ fontSize: 20 }}>
                  🌍
                </span>
                <input
                  type="text"
                  placeholder="Bölge"
                  value={newServis.region}
                  onChange={(e) =>
                    setNewServis({ ...newServis, region: e.target.value })
                  }
                  required
                  style={{
                    borderRadius: 6,
                    border: "1px solid #007bff",
                    padding: "0.5rem 1rem",
                  }}
                />
                <button
                  className="btn btn-success"
                  type="submit"
                  style={{ borderRadius: 6, fontWeight: 600 }}
                >
                  + Servis Ekle
                </button>
              </div>
            </form>
            {/* Düzenleme formu */}
            {editingServis && (
              <form
                onSubmit={handleEditServis}
                style={{ marginBottom: "2rem" }}
              >
                <div style={{ display: "flex", gap: "1rem" }}>
                  <input
                    type="text"
                    value={editingServis.name}
                    onChange={(e) =>
                      setEditingServis({
                        ...editingServis,
                        name: e.target.value,
                      })
                    }
                    required
                  />
                  <input
                    type="text"
                    value={editingServis.region}
                    onChange={(e) =>
                      setEditingServis({
                        ...editingServis,
                        region: e.target.value,
                      })
                    }
                    required
                  />
                  <button className="btn btn-primary" type="submit">
                    Kaydet
                  </button>
                  <button
                    className="btn btn-secondary"
                    type="button"
                    onClick={() => setEditingServis(null)}
                  >
                    İptal
                  </button>
                </div>
              </form>
            )}
          </div>
        )}
        {selected === "satis" && (
          <div
            style={{
              background: "#f4f8ff",
              borderRadius: 12,
              boxShadow: "0 2px 12px rgba(0,123,255,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>
              Satış İşlemleri
            </h2>
            <table
              className="table table-striped table-hover"
              style={{
                background: "#fff",
                borderRadius: 8,
                boxShadow: "0 1px 6px rgba(0,0,0,0.04)",
              }}
            >
              <thead className="table-primary">
                <tr>
                  <th>Ürün</th>
                  <th>Miktar</th>
                  <th>Tutar</th>
                  <th>Tarih</th>
                  <th>Durum</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Buzdolabı</td>
                  <td>3</td>
                  <td>45.000 ₺</td>
                  <td>15.09.2025</td>
                  <td>
                    <span className="badge bg-success">Teslim Edildi</span>
                  </td>
                </tr>
                <tr>
                  <td>Çamaşır Makinesi</td>
                  <td>2</td>
                  <td>28.000 ₺</td>
                  <td>14.09.2025</td>
                  <td>
                    <span className="badge bg-warning text-dark">
                      Hazırlanıyor
                    </span>
                  </td>
                </tr>
                <tr>
                  <td>Fırın</td>
                  <td>1</td>
                  <td>12.000 ₺</td>
                  <td>13.09.2025</td>
                  <td>
                    <span className="badge bg-danger">İptal</span>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        )}
        {selected === "cari" && (
          <div
            style={{
              background: "#f4f8ff",
              borderRadius: 12,
              boxShadow: "0 2px 12px rgba(0,123,255,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>
              Cari Hesaplar
            </h2>
            <table
              className="table table-striped table-hover"
              style={{
                background: "#fff",
                borderRadius: 8,
                boxShadow: "0 1px 6px rgba(0,0,0,0.04)",
              }}
            >
              <thead className="table-primary">
                <tr>
                  <th>Tarih</th>
                  <th>Gelir</th>
                  <th>Gider</th>
                  <th>Bakiye</th>
                  <th>Açıklama</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>15.09.2025</td>
                  <td>10.000 ₺</td>
                  <td>2.000 ₺</td>
                  <td>8.000 ₺</td>
                  <td>Servis geliri</td>
                </tr>
                <tr>
                  <td>14.09.2025</td>
                  <td>5.000 ₺</td>
                  <td>1.500 ₺</td>
                  <td>3.500 ₺</td>
                  <td>Yedek parça satışı</td>
                </tr>
                <tr>
                  <td>13.09.2025</td>
                  <td>0 ₺</td>
                  <td>500 ₺</td>
                  <td>-500 ₺</td>
                  <td>Ofis gideri</td>
                </tr>
              </tbody>
            </table>
          </div>
        )}
        {selected === "rapor" && (
          <div
            style={{
              background: "#f4f8ff",
              borderRadius: 12,
              boxShadow: "0 2px 12px rgba(0,123,255,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>
              Raporlama
            </h2>
            <table
              className="table table-striped table-hover"
              style={{
                background: "#fff",
                borderRadius: 8,
                boxShadow: "0 1px 6px rgba(0,0,0,0.04)",
              }}
            >
              <thead className="table-primary">
                <tr>
                  <th>Rapor Türü</th>
                  <th>Toplam Tutar</th>
                  <th>Dönem</th>
                  <th>Durum</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Satış Raporu</td>
                  <td>85.000 ₺</td>
                  <td>Eylül 2025</td>
                  <td>
                    <span className="badge bg-success">Başarılı</span>
                  </td>
                </tr>
                <tr>
                  <td>Cari Hesap Raporu</td>
                  <td>11.000 ₺</td>
                  <td>Ağustos 2025</td>
                  <td>
                    <span className="badge bg-warning text-dark">
                      İnceleniyor
                    </span>
                  </td>
                </tr>
                <tr>
                  <td>Servis Performans Raporu</td>
                  <td>2.500 ₺</td>
                  <td>Temmuz 2025</td>
                  <td>
                    <span className="badge bg-danger">Eksik</span>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        )}
      </main>
    </div>
  );
}
