import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AnaBayiPanel({ user, token }) {
  const [selected, setSelected] = useState("altbayiler");
  const [altBayiler, setAltBayiler] = useState([]);
  const [servisler, setServisler] = useState([]);
  const [satislar, setSatislar] = useState([]);
  const [cariHesaplar, setCariHesaplar] = useState([]);
  const [raporlar, setRaporlar] = useState([]);
  const [newAltBayi, setNewAltBayi] = useState({ name: "", region: "" });
  const [editingAltBayi, setEditingAltBayi] = useState(null);
  const [newServis, setNewServis] = useState({ name: "", region: "" });
  const [editingServis, setEditingServis] = useState(null);

  // Sadece kendi bölgesine bağlı alt bayiler ve servisler
  useEffect(() => {
    async function fetchData() {
      const region = user?.region;
      const altBayilerRes = await axios.get(`/api/altbayi?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAltBayiler(altBayilerRes.data);
      const servislerRes = await axios.get(`/api/servis?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServisler(servislerRes.data);
      const satislarRes = await axios.get(`/api/satis?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSatislar(satislarRes.data);
      const cariRes = await axios.get(`/api/cari?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCariHesaplar(cariRes.data);
      const raporRes = await axios.get(`/api/rapor?region=${region}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRaporlar(raporRes.data);
    }
    if (user?.region) fetchData();
  }, [user, token]);

  const handleAddAltBayi = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "/api/altbayi",
        { ...newAltBayi, createdBy: user._id },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAltBayiler([...altBayiler, res.data]);
      setNewAltBayi({ name: "", region: "" });
    } catch (err) {
      alert("Alt bayi eklenemedi!");
    }
  };

  const handleEditAltBayi = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(
        `/api/altbayi/${editingAltBayi._id}`,
        editingAltBayi,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAltBayiler(
        altBayiler.map((b) => (b._id === editingAltBayi._id ? res.data : b))
      );
      setEditingAltBayi(null);
    } catch (err) {
      alert("Alt bayi güncellenemedi!");
    }
  };

  const handleDeleteAltBayi = async (id) => {
    try {
      await axios.delete(`/api/altbayi/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAltBayiler(altBayiler.filter((b) => b._id !== id));
    } catch (err) {
      alert("Alt bayi silinemedi!");
    }
  };

  const handleAddServis = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "/api/servis",
        { ...newServis, createdBy: user._id },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setServisler([...servisler, res.data]);
      setNewServis({ name: "", region: "" });
    } catch (err) {
      alert("Servis eklenemedi!");
    }
  };

  const handleEditServis = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(
        `/api/servis/${editingServis._id}`,
        editingServis,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setServisler(
        servisler.map((s) => (s._id === editingServis._id ? res.data : s))
      );
      setEditingServis(null);
    } catch (err) {
      alert("Servis güncellenemedi!");
    }
  };

  const handleDeleteServis = async (id) => {
    try {
      await axios.delete(`/api/servis/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServisler(servisler.filter((s) => s._id !== id));
    } catch (err) {
      alert("Servis silinemedi!");
    }
  };

  return (
    <div className="anabayi-layout" style={{ display: "flex", minHeight: "100vh" }}>
      <aside className="sidebar" style={{ width: 220, background: "#f8f9fa", padding: "2rem 1rem", boxShadow: "0 2px 8px rgba(0,0,0,0.04)" }}>
        <h3 style={{ textAlign: "center", marginBottom: "2rem", fontWeight: 700, letterSpacing: 1, color: "#007bff" }}>Ana Bayi Paneli</h3>
        <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
          <li style={{ padding: "0.75rem 1rem", cursor: "pointer", borderBottom: "1px solid #ddd", background: selected === "altbayiler" ? "#007bff" : "transparent", color: selected === "altbayiler" ? "#fff" : "#000" }} onClick={() => setSelected("altbayiler")}>Alt Bayiler<div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>Bölgenize bağlı alt bayileri görüntüleyin ve ekleyin.</div></li>
          <li style={{ padding: "0.75rem 1rem", cursor: "pointer", borderBottom: "1px solid #ddd", background: selected === "servisler" ? "#007bff" : "transparent", color: selected === "servisler" ? "#fff" : "#000" }} onClick={() => setSelected("servisler")}>Yetkili Servisler<div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>Bölgenizdeki yetkili servisleri yönetin.</div></li>
          <li style={{ padding: "0.75rem 1rem", cursor: "pointer", borderBottom: "1px solid #ddd", background: selected === "satis" ? "#007bff" : "transparent", color: selected === "satis" ? "#fff" : "#000" }} onClick={() => setSelected("satis")}>Satış İşlemleri<div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>Bölgenize ait satış hareketlerini inceleyin.</div></li>
          <li style={{ padding: "0.75rem 1rem", cursor: "pointer", borderBottom: "1px solid #ddd", background: selected === "cari" ? "#007bff" : "transparent", color: selected === "cari" ? "#fff" : "#000" }} onClick={() => setSelected("cari")}>Cari Hesaplar<div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>Gelir, gider ve bakiye durumunu takip edin.</div></li>
          <li style={{ padding: "0.75rem 1rem", cursor: "pointer", borderBottom: "1px solid #ddd", background: selected === "rapor" ? "#007bff" : "transparent", color: selected === "rapor" ? "#fff" : "#000" }} onClick={() => setSelected("rapor")}>Raporlama<div style={{ fontSize: 12, color: "#555", marginTop: 2 }}>Performans ve finansal raporları görüntüleyin.</div></li>
          <li style={{ padding: "0.75rem 1rem", cursor: "pointer", borderBottom: "1px solid #ddd", background: "transparent", color: "#000" }} onClick={() => (window.location.href = "/")}>Çıkış Yap</li>
        </ul>
      </aside>
      <main style={{ flex: 1, background: "#fff", padding: "2rem 3rem" }}>
        {/* Tüm detaylı içerik blokları sadece yukarıdaki sade bloklarda gösterilecek. */}
        {selected === "satis" && (
          <div style={{ background: "#f4f8ff", borderRadius: 12, boxShadow: "0 2px 12px rgba(0,123,255,0.08)", padding: "2rem", marginBottom: "2rem" }}>
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>Satış İşlemleri</h2>
            {/* Yeni satış ekleme formu */}
            <form style={{ marginBottom: "2rem", background: "#fff", borderRadius: 8, boxShadow: "0 1px 6px rgba(0,0,0,0.04)", padding: "1rem" }}>
              <div className="row g-2">
                <div className="col-md-3"><input type="text" className="form-control" placeholder="Müşteri Adı" /></div>
                <div className="col-md-3"><input type="text" className="form-control" placeholder="Ürün Adı" /></div>
                <div className="col-md-2"><input type="number" className="form-control" placeholder="Adet" /></div>
                <div className="col-md-2"><input type="number" className="form-control" placeholder="Fiyat" /></div>
                <div className="col-md-2"><input type="date" className="form-control" /></div>
              </div>
              <div className="mt-2 text-end"><button type="button" className="btn btn-success">Satış Ekle</button></div>
            </form>
            {/* Toplam satış tutarı */}
            <div className="mb-2" style={{ fontWeight: 600, fontSize: 18, color: "#007bff" }}>Toplam Satış Tutarı: ₺35.000</div>
            {/* Statik satış listesi tablosu */}
            <div className="table-responsive">
              <table className="table table-bordered table-striped table-hover" style={{ background: "#fff", borderRadius: 8 }}>
                <thead className="table-primary">
                  <tr>
                    <th>Tarih</th>
                    <th>Müşteri</th>
                    <th>Ürün</th>
                    <th>Adet</th>
                    <th>Fiyat</th>
                    <th>Toplam</th>
                  </tr>
                </thead>
                <tbody>
                  <tr><td>01.09.2025</td><td>Ahmet</td><td>Buzdolabı</td><td>1</td><td>₺9000</td><td>₺9000</td></tr>
                  <tr><td>03.09.2025</td><td>Ayşe</td><td>Çamaşır</td><td>2</td><td>₺7000</td><td>₺14000</td></tr>
                  <tr><td>05.09.2025</td><td>Mehmet</td><td>TV</td><td>1</td><td>₺12000</td><td>₺12000</td></tr>
                </tbody>
              </table>
            </div>
          </div>
        )}
        {selected === "cari" && (
          <div style={{ background: "#f4f8ff", borderRadius: 12, boxShadow: "0 2px 12px rgba(0,123,255,0.08)", padding: "2rem", marginBottom: "2rem" }}>
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>Cari Hesap Takibi</h2>
            {/* Yeni cari işlem ekleme formu */}
            <form style={{ marginBottom: "2rem", background: "#fff", borderRadius: 8, boxShadow: "0 1px 6px rgba(0,0,0,0.04)", padding: "1rem" }}>
              <div className="row g-2">
                <div className="col-md-2"><select className="form-control"><option>Gelir</option><option>Gider</option></select></div>
                <div className="col-md-4"><input type="text" className="form-control" placeholder="Açıklama" /></div>
                <div className="col-md-2"><input type="number" className="form-control" placeholder="Tutar" /></div>
                <div className="col-md-2"><input type="date" className="form-control" /></div>
                <div className="col-md-2 text-end"><button type="button" className="btn btn-success">Ekle</button></div>
              </div>
            </form>
            {/* Statik cari hareket tablosu */}
            <div className="table-responsive">
              <table className="table table-bordered table-striped table-hover" style={{ background: "#fff", borderRadius: 8 }}>
                <thead className="table-primary">
                  <tr>
                    <th>Tarih</th>
                    <th>Açıklama</th>
                    <th>Tutar</th>
                    <th>Tür</th>
                  </tr>
                </thead>
                <tbody>
                  <tr><td>01.09.2025</td><td>Satış Geliri</td><td>₺9000</td><td>Gelir</td></tr>
                  <tr><td>02.09.2025</td><td>Kira Ödemesi</td><td>₺5000</td><td>Gider</td></tr>
                  <tr><td>03.09.2025</td><td>Satış Geliri</td><td>₺12000</td><td>Gelir</td></tr>
                </tbody>
              </table>
            </div>
            {/* Toplamlar */}
            <div className="mt-3 d-flex justify-content-between" style={{ fontWeight: 600, fontSize: 16 }}>
              <span>Toplam Gelir: ₺21.000</span>
              <span>Toplam Gider: ₺5.000</span>
              <span>Bakiye: ₺16.000</span>
            </div>
          </div>
        )}
        {selected === "rapor" && (
          <div style={{ background: "#f4f8ff", borderRadius: 12, boxShadow: "0 2px 12px rgba(0,123,255,0.08)", padding: "2rem", marginBottom: "2rem" }}>
            <h2 style={{ color: "#007bff", fontWeight: 600, marginBottom: 24 }}>Satış ve Cari Raporları</h2>
            {/* PDF ve Excel indir butonları */}
            <div className="mb-3 d-flex gap-2">
              <button className="btn btn-danger">PDF İndir</button>
              <button className="btn btn-success">Excel İndir</button>
            </div>
            {/* Satış raporu tablosu */}
            <div className="table-responsive mb-4">
              <table className="table table-bordered table-striped table-hover" style={{ background: "#fff", borderRadius: 8 }}>
                <thead className="table-primary">
                  <tr>
                    <th>Ürün</th>
                    <th>Toplam Adet</th>
                    <th>Toplam Satış</th>
                  </tr>
                </thead>
                <tbody>
                  <tr><td>Buzdolabı</td><td>10</td><td>₺90.000</td></tr>
                  <tr><td>Çamaşır</td><td>6</td><td>₺42.000</td></tr>
                  <tr><td>TV</td><td>4</td><td>₺48.000</td></tr>
                </tbody>
              </table>
            </div>
            {/* Cari raporu tablosu */}
            <div className="table-responsive mb-4">
              <table className="table table-bordered table-striped table-hover" style={{ background: "#fff", borderRadius: 8 }}>
                <thead className="table-primary">
                  <tr>
                    <th>Tür</th>
                    <th>Toplam</th>
                  </tr>
                </thead>
                <tbody>
                  <tr><td>Gelir</td><td>₺210.000</td></tr>
                  <tr><td>Gider</td><td>₺50.000</td></tr>
                  <tr><td>Bakiye</td><td>₺160.000</td></tr>
                </tbody>
              </table>
            </div>
            {/* Grafik placeholder */}
            <div style={{ height: 180, background: "#e9ecef", borderRadius: 8, display: "flex", alignItems: "center", justifyContent: "center", color: "#888", fontSize: 20 }}>Grafik Alanı (placeholder)</div>
          </div>
        )}
      </main>
    </div>
  );
}
