import React, { useState } from "react";
import axios from "axios";
import "../App.css";

// Placeholder API fonksiyonları
const fetchDealers = async () => [];
const fetchSubDealers = async () => [];
const fetchServices = async () => [];
const fetchAccounts = async () => [];
const fetchReports = async () => [];
const fetchUsers = async () => [];
const addUser = async (user) => {
  // Backend API'ye yeni kullanıcı ekle
  try {
    const res = await axios.post(
      "http://localhost:5000/api/auth/register",
      user
    );
    return res.data;
  } catch (err) {
    alert("Kullanıcı eklenemedi! " + (err.response?.data?.message || ""));
    return null;
  }
};

const menuItems = [
  { key: "dealers", label: "Ana Bayiler", icon: "🏢" },
  { key: "subdealers", label: "Alt Bayiler", icon: "🏬" },
  { key: "services", label: "Yetkili Servisler", icon: "🛠️" },
  { key: "accounts", label: "Cari Hesap Akışı", icon: "💳" },
  { key: "reports", label: "Raporlama", icon: "📊" },
  { key: "users", label: "Kullanıcı Yönetimi", icon: "👤" },
  { key: "logout", label: "Çıkış", icon: "🚪" },
];

export default function AdminPanelFull({ user, token }) {
  const [selected, setSelected] = useState("dealers");
  const [showAddUser, setShowAddUser] = useState(false);
  const [users, setUsers] = useState([
    { name: "superadmin", role: "Admin" },
    { name: "main_dealer", role: "Ana Bayi" },
    { name: "sub_dealer", role: "Alt Bayi" },
    { name: "admintest", role: "Admin" },
  ]);
  const [newUser, setNewUser] = useState({
    name: "",
    role: "Admin",
    email: "",
    password: "",
  });
  const [loggedOut, setLoggedOut] = useState(false);

  if (loggedOut) {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh",
        }}
      >
        <h2>Çıkış yapıldı.</h2>
        <p>Güvenli çıkış yaptınız.</p>
        <a href="/" className="btn btn-primary">
          Girişe Dön
        </a>
      </div>
    );
  }

  return (
    <div className="admin-layout">
      <aside className="sidebar">
        <h3>Admin Paneli</h3>
        <ul>
          {menuItems.map((item) => (
            <li
              key={item.key}
              className={selected === item.key ? "active" : ""}
              onClick={() =>
                item.key === "logout"
                  ? setLoggedOut(true)
                  : setSelected(item.key)
              }
              style={{ display: "flex", alignItems: "center", gap: "8px" }}
            >
              <span>{item.icon}</span> {item.label}
            </li>
          ))}
        </ul>
      </aside>
      <main className="main-content">
        <div style={{ position: "relative", marginBottom: "2rem" }}>
          <div
            style={{
              position: "absolute",
              top: 0,
              right: 0,
              display: "flex",
              alignItems: "center",
              gap: "1rem",
              background: "#fff",
              padding: "0.7rem 1.5rem",
              borderRadius: "8px",
              boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
            }}
          >
            <span
              style={{
                fontWeight: "bold",
                fontSize: "1.1rem",
                color: "#2a3a5e",
              }}
            >
              Hoşgeldiniz, {user?.name || "Admin"}
            </span>
            <button
              className="btn btn-danger"
              onClick={() => setLoggedOut(true)}
            >
              Çıkış Yap
            </button>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "2rem",
            marginTop: "3.5rem",
          }}
        >
          <div
            style={{
              flex: "1",
              minWidth: "340px",
              background: "#fff",
              borderRadius: "16px",
              boxShadow: "0 4px 16px rgba(0,0,0,0.08)",
              padding: "2rem",
              marginBottom: "2rem",
            }}
          >
            {selected === "dealers" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Ana Bayiler
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Bölge</th>
                    </tr>
                  </thead>
                  <tbody>
                    {dealers.map((d, i) => (
                      <tr key={i}>
                        <td>{d.name}</td>
                        <td>{d.region}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "subdealers" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Alt Bayiler
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Üst Bayi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {subdealers.map((d, i) => (
                      <tr key={i}>
                        <td>{d.name}</td>
                        <td>{d.parent}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "services" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Yetkili Servisler
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Üst Bayi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {services.map((s, i) => (
                      <tr key={i}>
                        <td>{s.name}</td>
                        <td>{s.parent}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            {selected === "accounts" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Cari Hesap Akışı
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Gelir</th>
                      <th>Gider</th>
                      <th>Bakiye</th>
                    </tr>
                  </thead>
                  <tbody>
                    {accounts.map((a, i) => (
                      <tr key={i}>
                        <td>{a.gelir}</td>
                        <td>{a.gider}</td>
                        <td>{a.bakiye}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div style={{ margin: "2rem 0" }}>
                  <h4>Grafik (placeholder)</h4>
                  <canvas
                    id="cariChart"
                    style={{
                      width: "100%",
                      height: "180px",
                      background: "#f8f9fa",
                      borderRadius: "8px",
                    }}
                  ></canvas>
                </div>
              </>
            )}
            {selected === "reports" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Raporlama
                </h2>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Tür</th>
                      <th>Tutar</th>
                      <th>Tarih</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reports.map((r, i) => (
                      <tr key={i}>
                        <td>{r.type}</td>
                        <td>{r.tutar}</td>
                        <td>{r.tarih}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div style={{ margin: "2rem 0" }}>
                  <button className="btn btn-success">Excel İndir</button>
                  <button
                    className="btn btn-primary"
                    style={{ marginLeft: "1rem" }}
                  >
                    PDF İndir
                  </button>
                </div>
              </>
            )}
            {selected === "users" && (
              <>
                <h2
                  className="panel-title"
                  style={{
                    color: "#007bff",
                    fontWeight: "600",
                  }}
                >
                  Kullanıcı Yönetimi
                </h2>
                <button
                  className="btn btn-primary mb-2"
                  onClick={() => setShowAddUser(true)}
                >
                  Yeni Kullanıcı Ekle
                </button>
                <table className="table">
                  <thead>
                    <tr>
                      <th>Ad</th>
                      <th>Rol</th>
                      <th>İşlem</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((u, i) => (
                      <tr key={i}>
                        <td>{u.name}</td>
                        <td>{u.role}</td>
                        <td>
                          <button className="btn btn-sm btn-warning">
                            Düzenle
                          </button>{" "}
                          <button className="btn btn-sm btn-danger">Sil</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                {showAddUser && (
                  <div
                    style={{
                      marginTop: "2rem",
                      background: "#f8f9fa",
                      padding: "1.5rem",
                      borderRadius: "8px",
                      boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                    }}
                  >
                    <h4>Yeni Kullanıcı Ekle</h4>
                    <form
                      onSubmit={async (e) => {
                        e.preventDefault();
                        const userToAdd = { ...newUser };
                        const result = await addUser(userToAdd);
                        if (result) {
                          setUsers([
                            ...users,
                            { name: result.name, role: result.role },
                          ]);
                          setShowAddUser(false);
                          setNewUser({
                            name: "",
                            role: "Admin",
                            email: "",
                            password: "",
                          });
                          alert("Kullanıcı başarıyla eklendi!");
                        }
                      }}
                    >
                      <div className="mb-3">
                        <label>Ad</label>
                        <input
                          type="text"
                          className="form-control"
                          required
                          value={newUser.name}
                          onChange={(e) =>
                            setNewUser({ ...newUser, name: e.target.value })
                          }
                        />
                      </div>
                      <div className="mb-3">
                        <label>Rol</label>
                        <select
                          className="form-control"
                          required
                          value={newUser.role}
                          onChange={(e) =>
                            setNewUser({ ...newUser, role: e.target.value })
                          }
                        >
                          <option>Admin</option>
                          <option>Ana Bayi</option>
                          <option>Alt Bayi</option>
                          <option>Servis</option>
                        </select>
                      </div>
                      <div className="mb-3">
                        <label>E-posta</label>
                        <input
                          type="email"
                          className="form-control"
                          required
                          value={newUser.email}
                          onChange={(e) =>
                            setNewUser({ ...newUser, email: e.target.value })
                          }
                        />
                      </div>
                      <div className="mb-3">
                        <label>Şifre</label>
                        <input
                          type="password"
                          className="form-control"
                          required
                          value={newUser.password}
                          onChange={(e) =>
                            setNewUser({ ...newUser, password: e.target.value })
                          }
                        />
                      </div>
                      <button className="btn btn-success" type="submit">
                        Kaydet
                      </button>
                      <button
                        className="btn btn-secondary"
                        type="button"
                        style={{ marginLeft: "1rem" }}
                        onClick={() => setShowAddUser(false)}
                      >
                        İptal
                      </button>
                    </form>
                  </div>
                )}
              </>
            )}
            {selected === "logout" && (
              <div style={{ textAlign: "center", padding: "4rem" }}>
                <h2>Çıkış yapıldı.</h2>
                <p>Güvenli çıkış yaptınız.</p>
                <a href="/" className="btn btn-primary">
                  Girişe Dön
                </a>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}
